const chroma = require('chroma-js');

module.exports = ([color, alpha]) => {
  if (typeof color !== 'string') {
    return null;
  }
  try {
    if (typeof alpha === 'number' && Number.isFinite(alpha)) {
      return chroma(color).alpha(alpha).css();
    }
    return chroma(color).css();
  } catch (error) {
    return null;
  }
};
