const chroma = require('chroma-js');

module.exports = ([color, blue]) => {
  if (typeof color !== 'string') {
    return null;
  }
  try {
    if (typeof blue === 'number' && Number.isFinite(blue)) {
      const rgba = chroma(color).rgba();
      rgba[2] = blue;
      return chroma(rgba).css();
    }
    return chroma(color).css();
  } catch (error) {
    return null;
  }
};
