const chroma = require('chroma-js');

module.exports = ([color, hue]) => {
  if (typeof color !== 'string') {
    return null;
  }
  try {
    if (typeof hue === 'number' && Number.isFinite(hue)) {
      const hsla = chroma(color).hsl();
      hsla[0] = hue;
      return chroma.hsl(hsla).css();
    }
    return chroma(color).css();
  } catch (error) {
    return null;
  }
};
