const { besseli } = require('../../engineering');

module.exports = ([x, n]) => {
  if (
    typeof n === 'number'
      && Number.isFinite(n)
      && typeof x === 'number'
      && Number.isFinite(x)
  ) {
    if (n < 0) {
      return null;
    }
    const result = besseli(x, n);
    return Number.isNaN(result) ? null : result;
  }
  return null;
};
