const { besselk } = require('../../engineering');

module.exports = ([x, n]) => {
  if (
    typeof n === 'number'
      && Number.isFinite(n)
      && typeof x === 'number'
      && Number.isFinite(x)
  ) {
    if (x <= 0 || n < 0) {
      return null;
    }
    const result = besselk(x, n);
    return Number.isNaN(result) ? null : result;
  }
  return null;
};
