const { RRI } = require('../../financial');

module.exports = ([periods, present, future]) => {
  if (periods == null || present == null || future == null) {
    return null;
  }
  if (typeof periods !== 'number'
      || !Number.isFinite(periods)
      || typeof present !== 'number'
      || !Number.isFinite(present)
      || typeof future !== 'number'
      || !Number.isFinite(future)
  ) {
    return null;
  }
  const result = RRI(periods, present, future);
  return Number.isFinite(result) ? result : null;
};
