const { SLN } = require('../../financial');

module.exports = ([cost, salvage, life]) => {
  if (cost == null || salvage == null || life == null) {
    return null;
  }
  if (typeof cost !== 'number'
      || !Number.isFinite(cost)
      || typeof salvage !== 'number'
      || !Number.isFinite(salvage)
      || typeof life !== 'number'
      || !Number.isFinite(life)
  ) {
    return null;
  }
  const result = SLN(cost, salvage, life);
  return Number.isFinite(result) ? Number(result.toFixed(2)) : null;
};
