module.exports = ([array, func]) => {
  if (!Array.isArray(array) || typeof func !== 'function') {
    return false;
  }
  let matchCount = 0;
  for (let i = 0; i < array.length; i += 1) {
    if (func(array[i], i)) {
      matchCount += 1;
      if (matchCount > 1) {
        return false;
      }
    }
  }
  return matchCount === 1;
};
