const { getArrayItemSchema } = require('@sapappgyver/appgyver-schemas');
const execute = require('./LAST_ITEM.runtime');

module.exports = {
  name: 'LAST_ITEM',
  category: 'List',
  title: 'Return the last item from a list',
  description: 'Returns the last item from the given list. If the list empty then returns undefined value.',
  parameters: [{
    type: 'array',
    title: 'List',
    description: 'The list from which to find the first item',
  }],
  analyze: ([arrayParam]) => ({
    schema: getArrayItemSchema(arrayParam && arrayParam.schema) || {},
  }),
  execute,
  examples: [
    { expression: 'LAST_ITEM([100, 3, 20])', result: 20 },
    { expression: 'LAST_ITEM(["foo", "bar"])', result: 'bar' },
    {
      context: {
        array: [{ a: 1 }, { a: 2 }, { a: 3 }],
      },
      expression: 'LAST_ITEM(array)',
      result: { a: 3 },
    },
  ],
};
