const { sort } = require('@sapappgyver/appgyver-schemas/utils/array');

module.exports = ([array, func, direction = 'asc']) => {
  if (!Array.isArray(array)) {
    return null;
  }
  if (typeof func !== 'function') {
    return array;
  }
  // TODO: Should be case-insensitive sort...? See also ORDER.invert.js
  return sort(array, direction, func, true);
};
