module.exports = ([array, func]) => {
  if (!Array.isArray(array) || typeof func !== 'function') {
    return null;
  }
  const removalIndex = array.findIndex((item, index) => func(item, index));
  if (removalIndex < 0) {
    return array;
  }
  const newArray = array.slice();
  newArray.splice(removalIndex, 1);
  return newArray;
};
