const SPLICE = require('./SPLICE.runtime');

module.exports = ([array, index, countRemove]) => {
  if (!Array.isArray(array) || !Number.isFinite(index)) {
    return null;
  }
  const intIndex = parseInt(index, 10);
  const intCountRemoveItems = Number.isFinite(countRemove) ? parseInt(countRemove, 10) : 1;
  if (intCountRemoveItems < 0) {
    return null;
  }


  return SPLICE([[...array], intIndex, intCountRemoveItems, []]);
};
