const execute = require('./REVERSE.runtime');

module.exports = {
  name: 'REVERSE',
  category: 'List',
  title: 'Reverse a list',
  description: 'Returns a list containing all the values from the given list, but in reverse order.',
  parameters: [{
    type: 'array',
    title: 'List',
    description: 'List to reverse',
    inversible: true,
  }],
  maxInversions: 1,
  analyze: ([arrayParam]) => {
    const arraySchema = (arrayParam && arrayParam.schema) || { type: 'array' };
    return {
      schema: arraySchema,
    };
  },
  execute,
  examples: [
    {
      expression: 'REVERSE(charArray)',
      context: {
        charArray: ['a', 'b', 'c'],
      },
      result: ['c', 'b', 'a'],
    },
    {
      expression: 'REVERSE(numbersArray)',
      context: {
        numbersArray: [1, 2, 3],
      },
      result: [3, 2, 1],
    },
    {
      expression: 'REVERSE(simpleArray)',
      context: {
        simpleArray: [1],
      },
      result: [1],
    },
  ],
};
