module.exports = ([array]) => {
  if (!Array.isArray(array)) {
    return null;
  }
  if (array.length <= 1) {
    return array;
  }
  const result = new Array(array.length);
  for (let i = 0; i < array.length; i += 1) {
    const j = Math.floor(Math.random() * (i + 1));
    if (i !== j) {
      result[i] = result[j];
    }
    result[j] = array[i];
  }
  return result;
};
