module.exports = ([array, index, countRemoveItems, newItems]) => {
  if (!Array.isArray(array) || !Array.isArray(newItems)
  || !Number.isFinite(index) || !Number.isFinite(countRemoveItems) || countRemoveItems < 0) {
    return null;
  }
  const result = [...array];
  const intIndex = parseInt(index, 10);
  const intCountRemoveItems = parseInt(countRemoveItems, 10);
  result.splice(intIndex, intCountRemoveItems, ...newItems);

  return result;
};
