module.exports = (inv) => {
  const { result } = inv;
  if (!Array.isArray(result)) {
    throw new Error('Cannot invert a non-list value');
  }
  const array1 = [];
  const array2 = [];
  result.forEach((item) => {
    if (!Array.isArray(item)) {
      throw new Error('ZIP cannot invert a list containing a non-list value');
    }
    if (item.length !== 2) {
      throw new Error('ZIP cannot invert a list containing a list with length other than 2');
    }
    array1.push(item[0]);
    array2.push(item[1]);
  });
  return inv.invertToParams([array1, array2]);
};
