const fact = require('../math/FACTORIAL.runtime');

module.exports = ([n, k]) => {
  if (
    typeof n === 'number'
      && Number.isFinite(n)
      && typeof k === 'number'
      && Number.isFinite(k)
  ) {
    if (n < k || n <= 0 || k < 0) {
      return null;
    }
    const nFact = fact([n]);
    const kFact = fact([k]);
    const nMinusKFact = fact([n - k]);
    if (Number.isFinite(nFact)
        && Number.isFinite(kFact)
        && Number.isFinite(nMinusKFact)) {
      const result = nFact / (kFact * nMinusKFact);
      return (Number.isFinite(result)) ? result : null;
    }
  }
  return null;
};
