const GCD = (a, b) => {
  let x = Math.abs(a);
  let y = Math.abs(b);
  while (y) {
    const t = y;
    y = x % y;
    x = t;
  }
  return x;
};
const LCM = (x, y) => ((!x || !y) ? 0 : Math.abs((x * y) / GCD(x, y)));
const LCMArray = (numbers) => {
  let a = numbers[0];
  numbers.forEach((number) => {
    a = LCM(a, number);
  });
  return a;
};

module.exports = ([array]) => {
  if (!Array.isArray(array)) {
    return null;
  }
  const validArray = array.filter(item => (typeof item === 'number' && Number.isFinite(item))).map(item => (Math.floor(item)));
  if (validArray.length) {
    return LCMArray(validArray);
  }
  return null;
};
