module.exports = ([num, base]) => {
  if (typeof num === 'number'
      && Number.isFinite(num)
      && typeof base === 'number'
      && Number.isFinite(base)
  ) {
    const lnNum = Math.log(num);
    const lnBase = Math.log(base);
    if (Number.isFinite(lnNum) && Number.isFinite(lnBase)) {
      const result = lnNum / lnBase;
      return Number.isFinite(result) ? result : null;
    }
  }
  return null;
};
