module.exports = ([array]) => {
  if (array == null) {
    return null;
  }
  if (array.length) {
    const maxValue = array.reduce((max, item) => {
      if (typeof item === 'number') {
        if (item > max) {
          return item;
        }
      }
      return max;
    }, -Infinity);
    return maxValue === -Infinity ? null : maxValue;
  }
  return null;
};
