module.exports = ([array]) => {
  if (array == null) {
    return null;
  }
  if (array.length) {
    const minValue = array.reduce((min, item) => {
      if (typeof item === 'number') {
        if (item < min) {
          return item;
        }
      }
      return min;
    }, +Infinity);
    return minValue === +Infinity ? null : minValue;
  }
  return null;
};
