module.exports = ([dividend, divisor]) => {
  if (dividend == null || divisor == null) {
    return null;
  }
  if (typeof dividend === 'number'
      && Number.isFinite(dividend)
      && typeof divisor === 'number'
      && Number.isFinite(divisor)
  ) {
    // NOTE: Modulo is NOT same than remainder.
    const mod = ((dividend % divisor) + divisor) % divisor;
    const result = Number.isFinite(mod) ? mod : null;
    return (result === 0) ? 0 : result; // fix returning -0
  }
  return null;
};
