module.exports = ([multiplicand, multiplier]) => {
  if (multiplicand == null || multiplier == null) {
    return null;
  }
  if (typeof multiplicand === 'number'
      && Number.isFinite(multiplicand)
      && typeof multiplier === 'number'
      && Number.isFinite(multiplier)
  ) {
    const multiply = multiplicand * multiplier;
    const result = Number.isFinite(multiply) ? multiply : null;
    return (result === 0) ? 0 : result; // fix returning -0
  }
  return null;
};
