module.exports = ([num]) => {
  if (typeof num === 'number' && Number.isFinite(num)) {
    const value = (Math.ceil(Math.abs(num)) % 2);
    if (value) {
      const result = Math.ceil(Math.abs(num));
      return num >= 0 ? result : -result;
    }
    const result = Math.ceil(Math.abs(num) + (Math.ceil(Math.abs(num)) % 2) + 1);
    return num >= 0 ? result : -result;
  }
  return null;
};
