module.exports = ([xValue, coeffs]) => {
  if (!Array.isArray(coeffs)) {
    return null;
  }
  if (typeof xValue !== 'number' || !Number.isFinite(xValue)) {
    return null;
  }
  const validCoeffs = coeffs.filter(item => (typeof item === 'number' && Number.isFinite(item)));
  const coeffsLength = validCoeffs.length;
  const result = validCoeffs.reduce((total, item, index) => {
    const poweredX = (xValue ** (coeffsLength - 1 - index));
    return total + poweredX * item;
  }, 0);
  return Number.isFinite(result) ? result : null;
};
