module.exports = ([array]) => {
  if (Array.isArray(array)) {
    const result = array.reduce((total, value) => {
      if (total != null) {
        return (typeof value === 'number' && Number.isFinite(value)) ? total * value : total;
      }
      return (typeof value === 'number' && Number.isFinite(value)) ? value : total;
    }, null);
    const finiteResult = result || 0;
    return Number.isFinite(finiteResult) ? finiteResult : null;
  }
  return 0;
};
