module.exports = ([count], { checkTimeout }) => {
  if (typeof count === 'number') {
    const countInt = Math.floor(count);
    if (Number.isFinite(countInt) && countInt >= 1) {
      const array = [];
      for (let i = 0; i < countInt; i++) {
        array.push(Math.random());
        checkTimeout();
      }
      return array;
    }
  }
  return null;
};
