module.exports = ([array]) => {
  if (array == null) {
    return null;
  }
  if (Array.isArray(array)) {
    const result = {};
    array.forEach((obj) => {
      if (typeof obj === 'object' && obj != null) {
        Object.keys(obj).forEach((prop) => {
          result[prop] = obj[prop];
        });
      }
    });
    return result;
  }
  return null;
};
