module.exports = ([object, arrProp]) => {
  const result = Object.assign({}, object);
  if (object !== null && typeof object === 'object'
    && Array.isArray(arrProp)) {
    const keysObject = Object.keys(object);
    const validArray = arrProp.filter(item => keysObject.includes(item));
    validArray.forEach((item) => {
      delete result[item];
    });
    return result;
  }
  return null;
};
