const { isEqual } = require('@sapappgyver/appgyver-schemas/utils/equality');

module.exports = ([object, value]) => {
  if (object !== null && typeof object === 'object') {
    const foundKey = Object.keys(object).find(key => isEqual(object[key], value));
    return (foundKey !== undefined) ? foundKey : null;
  }

  return null;
};
