const { resolveObjectMemberAccessor } = require('@sapappgyver/appgyver-schemas');

module.exports = ([schema, propertyName]) => {
  const accessorSchema = { type: 'string' };
  if (typeof propertyName === 'string') {
    accessorSchema.enum = [propertyName];
  }
  const member = resolveObjectMemberAccessor(schema, accessorSchema, true);
  return member.errors.length ? null : member.schema || {};
};
