const average = require('./AVERAGE.runtime');

const avedev = (array) => {
  const avg = average([array]);
  if (typeof avg === 'number' && Number.isFinite(avg)) {
    return array.reduce((total, item) => total + Math.abs(item - avg), 0) / array.length;
  }
  return null;
};

module.exports = ([array]) => {
  if (!Array.isArray(array)) {
    return null;
  }
  const validArray = array.filter(item => (typeof item === 'number' && Number.isFinite(item)));
  if (validArray.length) {
    const result = avedev(validArray);
    return Number.isFinite(result) ? result : null;
  }
  return null;
};
