const chisqDist = require('./CHISQ_DIST.runtime');

module.exports = ([num, degrees]) => {
  if (!(typeof num === 'number' && Number.isFinite(num))) {
    return null;
  }
  if (!(typeof degrees === 'number' && Number.isFinite(degrees))) {
    return null;
  }
  const result = 1 - chisqDist([num, degrees, true]);
  return Number.isFinite(result) ? result : null;
};
