module.exports = ([x, rate, cumulative]) => {
  if (
    typeof x === 'number'
      && Number.isFinite(x)
      && typeof rate === 'number'
      && Number.isFinite(rate)
  ) {
    if (x < 0 || rate <= 0) {
      return null;
    }
    const result = cumulative
      ? 1 - Math.exp(-rate * x)
      : rate * Math.exp(-rate * x);
    return Number.isFinite(result) ? result : null;
  }
  return null;
};
