module.exports = ([p, rate]) => {
  if (
    typeof p === 'number'
      && Number.isFinite(p)
      && typeof rate === 'number'
      && Number.isFinite(rate)
  ) {
    if (p <= 0 || p >= 1 || rate <= 0) {
      return null;
    }
    const result = -Math.log(1 - p) / rate;
    return Number.isFinite(result) ? result : null;
  }
  return null;
};
