const fDist = require('./F_DIST.runtime');

module.exports = ([value, degrees1, degrees2]) => {
  if (!(typeof value === 'number' && Number.isFinite(value))) {
    return null;
  }
  if (!(typeof degrees1 === 'number' && Number.isFinite(degrees1))) {
    return null;
  }
  if (!(typeof degrees2 === 'number' && Number.isFinite(degrees2))) {
    return null;
  }

  const leftTailedResult = fDist([value, degrees1, degrees2, true]);
  return Number.isFinite(leftTailedResult)
    ? 1 - fDist([value, degrees1, degrees2, true])
    : null;
};
