module.exports = ([array]) => {
  if (!Array.isArray(array)) {
    return null;
  }
  const arrayValid = array.filter(item => (typeof item === 'number' && Number.isFinite(item)));
  if (!arrayValid.length) {
    return null;
  }
  const sumResult = arrayValid.reduce((total, item) => total + 1 / item, 0);
  const result = Number.isFinite(sumResult) ? arrayValid.length / sumResult : null;
  return Number.isFinite(result) ? result : null;
};
