const erfcInv = require('./ERFC_INV.runtime');

module.exports = ([x, mean, std]) => {
  if (
    typeof x === 'number'
      && Number.isFinite(x)
      && typeof mean === 'number'
      && Number.isFinite(mean)
      && typeof std === 'number'
      && Number.isFinite(std)
  ) {
    if (x <= 0 || x >= 1) {
      return null;
    }
    if (std <= 0) {
      return null;
    }
    const result = Math.exp(-1.41421356237309505 * std * erfcInv([2 * x]) + mean);
    return Number.isFinite(result) ? result : null;
  }
  return null;
};
