const { median } = require('@sapappgyver/appgyver-schemas/utils/array');

module.exports = ([array]) => {
  if (!Array.isArray(array)) {
    return null;
  }
  const validArray = array.filter(val => (typeof val === 'number' && !Number.isNaN(val)));
  if (validArray.length) {
    const result = median(validArray);
    return result == null ? null : result;
  }
  return null;
};
