const erfcInv = require('./ERFC_INV.runtime');

module.exports = ([x, mean, std]) => {
  if (
    typeof x === 'number'
      && Number.isFinite(x)
      && typeof mean === 'number'
      && Number.isFinite(mean)
      && typeof std === 'number'
      && Number.isFinite(std)
  ) {
    const result = -1.41421356237309505 * std * erfcInv([2 * x]) + mean;
    return Number.isFinite(result) ? result : null;
  }
  return null;
};
