const sortArray = require('../list/SORT.runtime');

module.exports = ([array, rank]) => {
  if (!Array.isArray(array)) {
    return null;
  }
  const validArray = array.filter(item => (typeof item === 'number' && Number.isFinite(item)));
  if (!validArray.length) {
    return null;
  }
  if (rank < 1 || (!Number.isFinite(rank) && typeof rank !== 'number') || rank - 1 > validArray.length) {
    return null;
  }
  return sortArray([validArray, 'asc'])[rank - 1];
};
