const fact = require('../math/FACTORIAL.runtime');

module.exports = ([n, k]) => {
  if (
    typeof n === 'number'
      && Number.isFinite(n)
      && typeof k === 'number'
      && Number.isFinite(k)
  ) {
    if (n < k || n <= 0 || k < 0) {
      return null;
    }
    const upperFact = fact([n]);
    const lowerFact = fact([n - k]);
    const result = Number.isFinite(lowerFact) ? (upperFact / lowerFact) : null;
    return Number.isFinite(result) ? result : null;
  }
  return null;
};
