module.exports = ([n, k]) => {
  if (
    typeof n === 'number'
      && Number.isFinite(n)
      && typeof k === 'number'
      && Number.isFinite(k)
  ) {
    if (n <= 0 || k < 1) {
      return null;
    }
    const result = n ** k;
    return Number.isFinite(result) ? result : null;
  }
  return null;
};
