module.exports = ([x, mean, std]) => {
  if (
    typeof x === 'number'
      && Number.isFinite(x)
      && typeof mean === 'number'
      && Number.isFinite(mean)
      && typeof std === 'number'
      && Number.isFinite(std)
  ) {
    if (std <= 0) {
      return null;
    }
    const result = (x - mean) / std;
    return Number.isFinite(result) ? result : null;
  }
  return null;
};
