const tDistRT = require('./T_DIST_RT.runtime');

module.exports = ([x, degrees]) => {
  if (
    typeof x === 'number'
      && Number.isFinite(x)
      && typeof degrees === 'number'
      && Number.isFinite(degrees)
  ) {
    if (degrees < 1) {
      return null;
    }
    if (x < 0) {
      return null;
    }
    const result = 2 * tDistRT([x, degrees, true]);
    return Number.isFinite(result) ? result : null;
  }
  return null;
};
