const tDist = require('./T_DIST.runtime');

module.exports = ([x, degrees]) => {
  if (
    typeof x === 'number'
      && Number.isFinite(x)
      && typeof degrees === 'number'
      && Number.isFinite(degrees)
  ) {
    if (degrees < 1) {
      return null;
    }
    const result = 1 - tDist([x, degrees, true]);
    return Number.isFinite(result) ? result : null;
  }
  return null;
};
