const tInv = require('./T_INV.runtime');

module.exports = ([prob, degrees]) => {
  if (
    typeof prob === 'number'
      && Number.isFinite(prob)
      && typeof degrees === 'number'
      && Number.isFinite(degrees)
  ) {
    if (degrees < 1 || prob <= 0 || prob >= 1) {
      return null;
    }
    const prob2T = 1 - prob / 2;
    const result = tInv([prob2T, degrees]);
    return (Number.isFinite(result)) ? result : null;
  }
  return null;
};
