module.exports = ([p, shape, scale]) => {
  if (
    typeof p === 'number'
      && Number.isFinite(p)
      && typeof shape === 'number'
      && Number.isFinite(shape)
      && typeof scale === 'number'
      && Number.isFinite(scale)
  ) {
    if (p < 0 || p > 1 || shape <= 0 || scale <= 0) {
      return null;
    }
    const result = scale * ((-Math.log(1 - p)) ** (1 / shape));
    return Number.isFinite(result) ? result : null;
  }
  return null;
};
