module.exports = ([string]) => {
  if (string == null || typeof string !== 'string') {
    return null;
  }
  if (typeof string === 'string') {
    const str = string.toLowerCase();
    const chars = {
      a: '4',
      e: '3',
      i: '1',
      o: '0',
      s: '5',
      b: '8',
      g: '6',
      t: '7',
      z: '2',
    };
    const result = str.replace(/[abegiostz]/g, m => chars[m]);
    return result;
  }
  return null;
};
