module.exports = ([string, substring, newSubstring]) => {
  if (string == null || typeof string !== 'string') {
    return null;
  }
  if (
    substring == null
      || newSubstring == null
      || typeof substring !== 'string'
      || typeof newSubstring !== 'string') {
    return string;
  }
  return string.split(substring).join(newSubstring);
};
